const SearchCollectionContents = forwardRef(({ keyword }, ref) => {
  const { rootRef, targetRef } = ref.current;
  const [collection, setCollection] = useState([]);
  const [hashtags, setHashTags] = useState([]);
  const [isLoading, setIsLoading] = useState(true);
  const isLoadingRefCheck = useRef(null);
  const pageNum = useRef(0);
  const serverPageNum = useRef(0);
  const pageSize = useRef(6);
  const componentMounted = useRef(true);
  const requestCollectionMore = useCallback(async () => {
    try {
      setIsLoading(true);
      isLoadingRefCheck.current = true;
      const res = await axios({
        method: 'get',
        url: `/api/search/collection/${keyword}`,
        data: null,
        params: {
          page: pageNum.current,
          size: pageSize.current,
        },
      });
      if (componentMounted.current) {
        console.log(res.data);
        console.log('최초호출!!!!!!!');
        setCollection(prev => [...prev, ...res.data.data.content]);
        if (!res.data.data.last) pageNum.current++;
        serverPageNum.current = res.data.data.number;
        console.log('서버한테 데이터 받은후 !!! 페이지');
        console.log(pageNum.current);
        console.log(serverPageNum.current);
      }
    } catch (error) {
      console.log(error);
    } finally {
      if (componentMounted.current) setIsLoading(false);
      isLoadingRefCheck.current = false;
    }
  }, []);

  const requestCollection = useCallback(async () => {
    try {
      setIsLoading(true);
      isLoadingRefCheck.current = true;
      console.log('트라이!!@@');
      const res = await axios({
        method: 'get',
        url: `/api/search/collection/${keyword}/first`,
        data: null,
        params: {
          page: pageNum.current,
          size: pageSize.current,
          length: 10,
        },
      });
      if (componentMounted.current) {
        console.log(res);
        console.log('최초호출!!!!!!!');
        setCollection(prev => [...prev, ...res.data.data.data.content]);
        setHashTags(prev => [...prev, ...res.data.data.hashtags]);
        if (!res.data.data.data.last) pageNum.current++;
        serverPageNum.current = res.data.data.data.number;
        console.log('서버한테 데이터 받은후 !!! 페이지');
        console.log(pageNum.current);
        console.log(serverPageNum.current);
      }
    } catch (error) {
      console.log('에러 발생@#@#@#');
      console.log(error);
    } finally {
      if (componentMounted.current) setIsLoading(false);
      isLoadingRefCheck.current = false;
    }
  }, []);
  useIntersectionObserver({
    root: rootRef.current,
    target: targetRef.current,
    onIntersect: useCallback(([{ isIntersecting }]) => {
      console.log('옵저버 isloading');
      //console.log(refLoading.current);
      console.log('옵저버 intersection');
      console.log(isIntersecting);
      if (
        isIntersecting &&
        !isLoadingRefCheck.current &&
        pageNum.current > serverPageNum.current
      ) {
        console.log('옵저버에서 리퀘스트');
        requestCollectionMore();
      }
    }, []),
  });
  useEffect(() => {
    componentMounted.current = true;
    console.log('마운트 후 !!!');
    requestCollection();
    console.log('최초호출!!!!!!!');
    console.log(collection);
    return () => {
      componentMounted.current = false;
    };
  }, []);
  console.log(hashtags);
  console.log(dummyTags);
  return (
    <Container>
      <LoadingState show={isLoading} />
      <SearchResultHashTags contents={dummyTags} />
      <SearchCollectionMasonry type={'collection'} contents={dummies} />
    </Container>
  );
});

SearchCollectionContents.displayName = 'SearchCollectionContents';
SearchCollectionContents.propTypes = {
  keyword: propTypes.string,
};

export default SearchCollectionContents;
